 \"type\": \"list\"}, {\"id\": \"gnlDPl_Knm\", \"data\": {\"text\": \"Advantages\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"LHKG60U7YV\", \"data\": {\"meta\": {}, \"items\": [{\"meta\": {}, \"items\": [], \"content\": \"Eliminates external fragmentation.\"}, {\"meta\": {}, \"items\": [], \"content\": \"Simplifies memory allocation.\"}], \"style\": \"unordered\"}, \"type\": \"list\"}, {\"id\": \"PBYQvn_4Xp\", \"data\": {\"text\": \"Challenges\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"h6sxdICr3y\", \"data\": {\"meta\": {}, \"items\": [{\"meta\": {}, \"items\": [], \"content\": \"Requires additional memory for page tables.\"}, {\"meta\": {}, \"items\": [], \"content\": \"Slower access due to address translation.\"}], \"style\": \"unordered\"}, \"type\": \"list\"}, {\"id\": \"6ydSDw-1ut\", \"data\": {\"text\": \"Conclusion\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"SMYQI1Oqv0\", \"data\": {\"text\": \"Paging is a cornerstone of modern memory management, ensuring efficient use of memory resources while avoiding fragmentation.\"}, \"type\": \"paragraph\"}], \"version\": \"2.30.7\"}','2024-12-02 14:18:56.215952','Paging: Simplifying Memory Management','2024-12-02 14:18:56.216001',26,8),(72,'{\"time\": 1733123952301, \"blocks\": [{\"id\": \"8bgbd8NwvM\", \"data\": {\"text\": \"Introduction\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"Cr7rKFyWiO\", \"data\": {\"text\": \"Virtual memory allows the operating system to use disk space as an extension of RAM, enabling the execution of larger programs than the physical memory can accommodate.\"}, \"type\": \"paragraph\"}, {\"id\": \"dqq34cPH-4\", \"data\": {\"text\": \"How Virtual Memory Works\", \"level\": 3}, \"type\": \"header\"}, {\"id\": \"6NN8y1vxH7\", \"data\": {\"meta\": {}, \"items\": [{\"meta\": {}, \"items\": [], \"content\": \"Processes are divided into pages stored on disk.\"}, {\"meta\": {}, \"items\": [], \"content\": \"Pages are loaded into RAM as needed (demand paging).\"}, {\"me